#include <stdint.h>
#include "main.h"

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

int main(void)
{
	// enable GPIO D clock
	RCC_AHB1ENR_t volatile *gpioDClkEn = ADDR_RCC_AHB1ENR_t;
	gpioDClkEn->gpio_d_en = CLOCK_ENABLE;

	// SET 12 pin as output
	GPIOx_MODE_REG_t volatile *gpioDMode = GPIOx_MODE_REG ;

	gpioDMode->pin_mode_r12 = MODE_CONFIG_OUTPUT; // set pin 12 as output mode

	// Modify the pin data
	GPIOx_ODR_t volatile *gpio_odr = ADDR_OUTPUT_DATA_REG ;
	gpio_odr->odr12 = 0x01;

    /* Loop forever */
	for(;;);
}
